package lesson6_2.Task1;import lesson6_2.Task1.Interfaces.CPU;import lesson6_2.Task1.Interfaces.Keyboard;import lesson6_2.Task1.Interfaces.Memory;import lesson6_2.Task1.Interfaces.Mouse;import lesson6_2.Task1.Interfaces.RAM;import lesson6_2.Task1.Interfaces.Screen;import lesson6_2.Task1.Interfaces.SoundProducer;import lesson6_2.Task1.Interfaces.VideoAdapter;public class PC {    private Mouse mouse;    private Keyboard keyboard;    private Screen screen;    private SoundProducer soundProducer;    private Memory memory;    private CPU cpu;    private RAM ram;    private VideoAdapter videoAdapter;    PC(Mouse mouse, Keyboard keyboard, Screen screen, SoundProducer soundProducer, Memory memory, CPU cpu, RAM ram, VideoAdapter videoAdapter) {        this(mouse, keyboard, screen, memory, cpu, ram);        this.soundProducer = soundProducer;        this.videoAdapter = videoAdapter;    }    PC(Mouse mouse, Keyboard keyboard, Screen screen, Memory memory, CPU cpu, RAM ram) {        this.mouse = mouse;        this.keyboard = keyboard;        this.screen = screen;        this.memory = memory;        this.cpu = cpu;        this.ram = ram;    }    PC(Memory memory, CPU cpu, RAM ram, VideoAdapter videoAdapter) {        this.memory = memory;        this.cpu = cpu;        this.ram = ram;        this.videoAdapter = videoAdapter;    }    public void show() {        try {            this.mouse.click();            this.keyboard.typeText();            this.screen.getImage();        } catch (NullPointerException e) {        }        try {            this.soundProducer.produceSound();        } catch (NullPointerException e) {        }        this.memory.getInformation();        this.cpu.calculate();        this.ram.getInformation();        try {            this.videoAdapter.renderVideo();        } catch (NullPointerException e) {        }    }}